package me.mbc.entity;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;import java.io.Serializable;@Entity@Table(name = "user_receiving_address", schema = "test")@Datapublic class UserReceivingAddressEntity  implements Serializable {    @Id    @Column(name = "id", nullable = false)    private Integer id;    @Column(name = "user_id", nullable = false)    private Integer userId;    @Column(name = "address_city", nullable = true, length = 500)    private String addressCity;}