package me.mbc.entity.customize;import lombok.Data;import lombok.NoArgsConstructor;import lombok.experimental.Accessors;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import javax.persistence.*;import java.util.Date;@Entity@Table(name = "address")@Data@NoArgsConstructor@Accessors(chain = true)public class Address {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String location;    @Column(name="create_date")    @CreationTimestamp    private Date createDate;    @Column(name="update_date")    @UpdateTimestamp    private Date updateDate;    /**     * admin_id：Address对应表的字段名     * id: Admin表中的字段名     */    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name="admin_id", referencedColumnName = "id")    private Admin admin;}