package me.mbc.repository.customize;import org.springframework.data.repository.NoRepositoryBean;import org.springframework.data.repository.Repository;import java.io.Serializable;import java.util.Optional;// 选择性暴露CRUD方法/** * 继承其他 Repository 的子接口，或者自定义子接口， * 可以选择性的暴漏 SimpleJpaRepository 里面已经实现的基础公用方法。 */@NoRepositoryBeanpublic interface MyBaseRepositoryBeanInterface<T, ID extends Serializable>        extends Repository<T, ID> {    Optional<T> findById(ID id);    <S extends T> S save(S entity);}