package me.mbc.repository.customize;import me.mbc.entity.customize.Admin;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.util.Date;import java.util.Optional;@RunWith(SpringRunner.class)@SpringBootTestpublic class TestAdminJpaRepository {    @Autowired    AdminJpaRepository adminJpaRepository;    @Test    public void testSave(){        Admin admin = new Admin().setName("Mary").setBirthDate(new Date());        adminJpaRepository.save(admin);    }    @Test    public void testFindById(){        Optional<Admin> admin = adminJpaRepository.findById(1L);        if(admin.isPresent()){            System.out.println("----------------------------");            System.out.println(admin.get().getName());            System.out.println(admin.get().getBirthDate());            System.out.println(admin.get().getUpdateDate());            System.out.println("----------------------------");        }    }}